import { useState ,useEffect } from "react";
const useCounter = () => {
  const [counter, setCounter] = useState(0);

  useEffect((counterUpdate) => {
    const interval = setInterval(() => {
      setCounter(c);
    }, 1000);

    return () => clearInterval(interval);
  }, []);
  return counter;
};

export default useCounter;
